mapper.select:
SELECT badge_no AS badgeNo, badge_status AS badgeStatus, party_code AS partyCode, local_authority_ref As localAuthorityRef, app_date AS appDate, app_channel_code AS appChannelCode, start_date AS startDate, expiry_date AS expiryDate, eligibility_code AS eligibilityCode, image_link AS imageLink, deliver_to_code AS deliverToCode, deliver_option_code AS deliverOptionCode, holder_name AS holderName, nino AS nino, dob AS dob, gender_code AS genderCode, contact_name AS contactName, contact_building_street AS contactBuildingStreet, contact_line2 AS contactLine2, contact_town_city AS contactTownCity, contact_postcode AS contactPostcode, primary_phone_no AS primaryPhoneNo, secondary_phone_no AS secondaryPhoneNo, contact_email_address AS contactEmailAddress, cancel_reason_code as cancelReasonCode, local_authority_short_code AS localAuthorityShortCode FROM badge WHERE
holder_name_upper LIKE #{name}
contact_postcode = #{postcode}
ORDER BY badge.start_date DESC LIMIT 50

mapper.update:
UPDATE badge
  SET badge_status = 'CANCELLED', cancel_reason_code = #{cancelReasonCode}
WHERE badge_no = #{badgeNo}
AND expiry_date >= current_date
AND badge_status = 'ISSUED'

    AND local_authority_short_code = #{localAuthorityShortCode}
